{
    "id": "urn:ngsi-ld:Subscription:MyAwesomeSubscription",
    "type": "Subscription",
    "description": "Notifies me of all air quality level changes",
    "entities": [
        {
            "idPattern": ".*",
            "type": "AirQualityObserved"
        }
    ],
    "watchedAttributes": [
        "airQualityLevel"
    ],
    "notification": {
        "attributes": [
            "id",
            "type",
            "airQualityLevel"
        ],
        "format": "keyValues",
        "endpoint": {
            "uri": "http://{{subscription_host}}:{{subscription_port}}/subscription/air-quality-level-change",
            "accept": "application/ld+json"
        }
    },
    "@context": [
        "https://schema.lab.fiware.org/ld/context",
        "https://uri.etsi.org/ngsi-ld/v1/ngsi-ld-core-context.jsonld"
    ]
}

{
    "id": "urn:ngsi-ld:ContextSourceRegistration:MyAwesomeContextSourceRegistration",
    "type": "ContextSourceRegistration",
    "description": "Resgisters a context source for my AirQualityObserved entities",
    "information": [
        {
            "entities": [
                {
                    "idPattern": ".*",
                    "type": "AirQualityObserved"
                }
            ],
            "properties": [
                "id",
                "type",
                "airQualityLevel",
                "dateObserved"
            ],
            "relationships": [
                "refPointOfInterest"
            ]
        }
    ],
    "endpoint": "http://my.ownbroker.org:1026",
    "@context": [
        "https://schema.lab.fiware.org/ld/context",
        "https://uri.etsi.org/ngsi-ld/v1/ngsi-ld-core-context.jsonld"
    ]
}

{
    "id": "urn:ngsi-ld:Subscription:MyAwesomeCsourceSubscription",
    "type": "Subscription",
    "description": "Notifies me of all context source registrations potentially providing information for AirQualityObserved entities I'm interested in",
    "entities": [
        {
            "idPattern": ".*",
            "type": "AirQualityObserved"
        }
    ],
    "notification": {
        "attributes": [
            "airQualityLevel",
            "dateObserved"
        ],
        "format": "keyValues",
        "endpoint": {
            "uri": "http://{{subscription_host}}:{{subscription_port}}/csource-subscription/air-quality-observed-csource",
            "accept": "application/ld+json"
        }
    },
    "@context": [
        "https://schema.lab.fiware.org/ld/context",
        "https://uri.etsi.org/ngsi-ld/v1/ngsi-ld-core-context.jsonld"
    ]
}
